definition.script:
pipeline {
    agent any

    parameters {
        credentials(
            credentialType: 'com.cloudbees.plugins.credentials.impl.UsernamePasswordCredentialsImpl',
            defaultValue: '',
            description: 'Cloudify credentials',
            name: 'cfy_credentials',
            required: true)
        string(name: 'deployment_id', description: 'Cloudify deployment ID', trim: true)
        string(name: 'k8s_master_host', description: 'Kubernetes Master host', trim: true)
        credentials(
            defaultValue: '',
            description: 'GCP Service Account',
            name: 'gcp_credentials',
            required: true)
        string(name: 'app_namespace', description: 'Application namespace', trim: true)
        text(name: 'k8s_app_definition', description: 'Kubernetes application definition')
    }

    stages {
        stage('Build Kubernetes Application') {
            steps {
                cfyKubernetes credentialsId: "${params.cfy_credentials}",
                    definitionAsString: "${params.k8s_app_definition}",
                    deploymentId: "${params.deployment_id}",
                    echoEnvData: true,
                    echoInputs: true,
                    envDataLocation: 'env-data.json',
                    gcpCredentialsId: "${params.gcp_credentials}",
                    k8sMaster: "${params.k8s_master_host}",
                    namespace: "${params.app_namespace}",
                    skipSslVerification: true,
                    validateStatus: true
            }
        }
        stage('Run tests') {
            steps {
                input message: 'Proceed?'
            }
        }
        stage('Delete Kubernetes Environment') {
            steps {
                deleteCloudifyEnv credentialsId: "${params.cfy_credentials}",
                    deleteBlueprintIfLast: false,
                    deploymentId: "${params.deployment_id}"
            }
        }
    }
}
